import requests
import json

def validatePassword(token):
    URL = 'https://senhcs.eduro.go.kr/v2/validatePassword'

    headers = {
        "Authorization": token,
        'Content-Type': 'application/json'
    }

    datas = {
        "deviceUuid": "",
        "password": ""
    }
    response = requests.post(URL, headers=headers, data=json.dumps(datas)).text
    
    return response

